    bf hi { 
        ++++++++[>++++[>++>+++>+++>+<<<<-]>
        +>+>->>+[<]<-]>>.>---.+++++++..+++.
        >>.<-.<.+++.------.--------.>>+.>++. 
    }

    say hi.decode; # Hello World!

    bf plus-two {
        ,++.
    }

    say plus-two(buf8.new: 40).head; # 42

